INSERT INTO users.services (id, service_name, description, price, is_active, created_at, updated_at, image, service_type, service_name_en, service_name_ko, description_ko, description_en, has_child) VALUES
(1, 'Massage', 'Na Spa sẽ giúp bạn giải tỏa căng thẳng mệt mỏi, cải thiện tuần hoàn máu & giấc ngủ với liệu pháp thư giãn bằng mùi hương', 0, true, '2025-05-05 17:42:25.771746+00', '2025-05-07 15:10:53.190935+00', 'uploads/services/1-c22199c3-cedc-4137-8782-973a8915703c.webp', NULL, 'Massage', NULL, NULL, 'Na Spa will help you relieve stress and fatigue, improve blood circulation and sleep quality through aroma relaxation therapy.', false),
(2, 'Chăm sóc da', 'Bao gồm các liệu trình: Sạch sâu cấp ẩm, Căng bóng da, Trị mụn với thời lượng từ 60 - 90 phút / liệu trình', 0, true, '2025-05-05 17:48:19.877074+00', '2025-05-07 15:11:54.594433+00', 'uploads/services/1-28a00245-8be7-4af3-94fb-4ebd6beeb9db.webp', NULL, 'Skincare', NULL, NULL, 'Deep cleansing and hydration, skin brightening, and acne treatment, each lasting 60 to 90 minutes per session.', false),
(3, 'Triệt lông', 'Na spa sử dụng công nghệ Triệt lông Diode Laser hiện đại nhất và an toàn cho da, triệt tận gốc lông và không sưng, đau.', 0, true, '2025-05-05 17:49:16.589262+00', '2025-05-07 15:13:22.510422+00', 'uploads/services/1-b8cb22ac-a711-4ac4-a3f8-ed6f4151767b.webp', NULL, 'Hair Removal', NULL, NULL, 'Na spa use the latest and safest Diode Laser hair removal technology, which eliminates hair at the root without causing swelling or pain.', false),
(4, 'Gội đầu dưỡng sinh', 'Sở hữu da đầu khỏe mạnh và mái tóc óng mượt. Cải thiện sức khỏe, giảm lo âu, mệt mỏi', 0, true, '2025-05-05 17:51:01.876655+00', '2025-05-07 17:05:55.896238+00', 'uploads/services/1-b0372090-9cc8-4b28-9bde-a191d35d5038.webp', NULL, 'Scalp Therapy', NULL, NULL, 'Achieve a healthy scalp and shiny, smooth hair. Improve your overall well-being while reducing stress and fatigue.', false),
(5, 'Nail', 'Dịch vụ chăm sóc và trang trí móng tay, móng chân giúp tôn lên vẻ đẹp và thể hiện cá tính.', 0, true, '2025-05-05 18:42:52.927206+00', '2025-05-07 15:15:09.010107+00', 'uploads/services/1-27827a75-a19c-434e-8d86-19cb553a9f76.jpg', NULL, 'Nail', NULL, NULL, 'Nail care and decoration services that enhance your beauty and express your unique personality.', false);
INSERT INTO users.service_items (id, parent_service_id, service_name, image, service_name_en, service_name_ko, description, service_type, description_en, description_ko, price, is_active, created_at, updated_at) VALUES
(1, 1, 'Massage body đá muối', 'uploads/services/1-3e7ac422-f934-4c20-a1e0-6e1e5dd70c07.webp', 'Salt stone body massage', NULL, 'Liệu pháp massage toàn thân bằng đá muối ấm giúp thư giãn và thải độc cơ thể.', NULL, 'A relaxing full-body massage using warm Himalayan salt stones to relieve tension and detoxify the body.', NULL, 350000, true, '2025-05-11 07:44:13.30142+00', '2025-05-11 08:36:04.114326+00'),
(2, 1, 'Massage cổ vai gáy', 'uploads/services/1-5baee80c-50fc-4b6b-b0b6-4ef23dd26666.webp', 'Neck & Shoulder Massage', NULL, 'Giúp giảm căng cứng, đau mỏi vùng cổ, vai, gáy – phù hợp cho người thường xuyên stress hoặc ngồi lâu.', NULL, 'Relieves tension and stiffness in the neck, shoulders, and upper back—ideal for stress and posture-related pain.', NULL, 280000, true, '2025-05-11 07:46:50.430944+00', '2025-05-11 08:37:24.3925+00'),
(3, 1, 'Massage thuỵ điển', 'uploads/services/1-7b681016-becd-496a-807e-60d93327b951.webp', 'Swedish Massage', NULL, 'Liệu pháp massage nhẹ nhàng toàn thân giúp thư giãn, tăng tuần hoàn máu và giảm căng cơ.', NULL, 'A gentle, relaxing full-body massage that improves circulation, reduces stress, and eases muscle tension.', NULL, 300000, true, '2025-05-11 07:51:16.172461+00', '2025-05-11 08:38:23.666665+00'),
(4, 2, 'Chăm sóc da mặt cơ bản', 'uploads/services/1-4ffba486-f42c-495b-a3e1-6f56068c9067.webp', 'Basic Facial', NULL, 'Liệu trình làm sạch, tẩy tế bào chết và dưỡng ẩm giúp làn da sáng khỏe, mịn màng.', NULL, 'A gentle skincare treatment that cleanses, exfoliates, and moisturizes the skin for a fresh, healthy glow.', NULL, 250000, true, '2025-05-11 07:55:12.935828+00', '2025-05-11 08:38:45.631278+00'),
(5, 2, 'Chăm sóc da mặt chuyên sâu', 'uploads/services/1-84104fde-8e8b-4a95-84a4-2b98983c72af.webp', 'Deep Cleansing Facial', NULL, 'Liệu trình chăm sóc da chuyên sâu giúp làm sạch sâu, tẩy tế bào chết, hút mụn và nuôi dưỡng làn da khỏe mạnh, sáng mịn.', NULL, 'An intensive skincare treatment that deeply cleanses, exfoliates, extracts impurities, and nourishes the skin for a clear and radiant complexion.', NULL, 450000, true, '2025-05-11 07:57:46.166727+00', '2025-05-11 08:38:56.893044+00'),
(6, 4, 'Gội đầu dưỡng sinh', 'uploads/services/1-e94fbbc9-22ee-409c-bbd4-fbbcf018f5b2.webp', 'Traditional Scalp Massage1', NULL, 'Liệu pháp gội đầu thư giãn, kích thích tuần hoàn máu, nuôi dưỡng tóc từ chân, giúp tóc khỏe mạnh và da đầu thư giãn.', NULL, 'A soothing treatment that promotes relaxation, improves blood circulation to the scalp, and nourishes hair follicles for healthier hair.', NULL, 150000, true, '2025-05-11 07:59:28.281272+00', '2025-05-11 08:41:27.737259+00'),
(7, 3, 'Triệt lông 1/2 chân', 'uploads/services/1-9433ecf4-f711-4824-bede-24c53d41c012.webp', 'Half-Leg Hair Removal', NULL, 'Loại bỏ lông vùng nửa chân (trên hoặc dưới) giúp da mịn màng, sạch lông lâu dài.', NULL, 'Effectively removes unwanted hair from the lower or upper legs, leaving skin smooth and long-lastingly hair-free.', NULL, 2400000, true, '2025-05-11 08:43:21.631907+00', '2025-05-11 08:43:21.631907+00'),
(8, 3, 'Triệt lông 1/2 tay', 'uploads/services/1-eb33b3a0-c3ba-47f2-b499-524da3b9fe31.webp', 'Half-Arm Hair Removal', NULL, 'Loại bỏ lông vùng nửa cánh tay (trên hoặc dưới) giúp làn da sạch lông, mịn màng và lâu mọc lại.', NULL, 'Removes unwanted hair from the upper or lower arms for smooth, hair-free skin that lasts.', NULL, 1600000, true, '2025-05-11 08:44:42.934923+00', '2025-05-11 08:44:42.934923+00'),
(9, 3, 'Triệt lông toàn thân', 'uploads/services/1-804d10c6-8695-477f-847a-76a31657e05b.webp', 'Full Body Hair Removal', NULL, 'Liệu trình triệt lông toàn diện trên toàn cơ thể, mang lại làn da mịn màng và hiệu quả lâu dài.', NULL, 'Comprehensive hair removal treatment covering all major body areas for smooth, long-lasting results.', NULL, 10500000, true, '2025-05-11 08:47:06.129445+00', '2025-05-11 08:47:06.129445+00'),
(10, 5, 'Làm móng tay', 'uploads/services/1-f23bf8d8-4de6-42c0-a56b-53da6868a13e.jpg', 'Manicure', NULL, 'Chăm sóc móng tay gồm cắt dũa, làm sạch, chăm sóc da quanh móng và sơn (nếu có) giúp bàn tay đẹp và gọn gàng', NULL, 'A nail care treatment that includes cleaning, shaping, cuticle care, and optional polish for healthy, beautiful hands.', NULL, 250000, true, '2025-05-11 08:48:39.494461+00', '2025-05-11 08:48:39.494461+00'),
(11, 5, 'Làm móng chân', 'uploads/services/1-419b689a-e935-460c-be0e-06536d41f3df.jpg', 'Pedicure', NULL, 'Chăm sóc móng chân gồm ngâm, làm sạch, cắt dũa, tẩy da chết và sơn (nếu có), giúp đôi chân mềm mại và gọn gàng', NULL, 'A foot and toenail care treatment that includes soaking, cleaning, shaping, exfoliation, and optional polish for soft, well-groomed feet.', NULL, 250000, true, '2025-05-11 08:49:27.541713+00', '2025-05-11 08:49:27.541713+00'); 
